from sympy import sqrt

def calculate_distance(x1, y1, x2, y2):
    dx = x2 - x1
    dy = y2 - y1
    distance = sqrt(dx**2 + dy**2)
    return float(distance)

calculate_sum_of_n_terms.code:
def calculate_sum_of_n_terms(sequence_type, first_term, common_difference, num_terms):
    if sequence_type == 'arithmetic':
        return num_terms / 2 * (2 * first_term + (num_terms - 1) * common_difference)
    elif sequence_type == 'geometric':
        if common_difference == 1:
            return first_term * num_terms
        else:
            return first_term * (1 - common_difference**num_terms) / (1 - common_difference)
    else:
        return None

