from sympy import symbols, Eq, solve

def solve_linear_equation_system(eq1, eq2):
    x, y = symbols('x y')
    equation1 = Eq(eval(eq1), 0)
    equation2 = Eq(eval(eq2), 0)
    return solve((equation1, equation2), (x, y))

