def calculate_average_cost(quantities, prices):
    if len(quantities) != len(prices):
        return "Error: Length of quantities and prices must match."
    total_cost = sum(q * p for q, p in zip(quantities, prices))
    total_quantity = sum(quantities)
    return total_cost / total_quantity if total_quantity > 0 else 0

