from sympy import symbols, Eq, solve

def solve_system_of_equations(equations):
    variables = symbols(' '.join(['var'+str(i) for i in range(len(equations))]))
    eqs = []
    for i, eq in enumerate(equations):
        eqs.append(Eq(eval(eq), 0))
    return solve(eqs, variables)
