def calculate_geometric_progression():
    largest_geo_number = 0

    # Loop through digit a from 9 to 1
    for a in range(9, 0, -1):
        for b in range(10):
            for c in range(10):
                if a != b and a != c and b != c:  # Ensure digits are distinct
                    if b**2 == a * c:  # Check for geometric sequence
                        number = a * 100 + b * 10 + c
                        if number > largest_geo_number:
                            largest_geo_number = number
    return largest_geo_number

