calculate_distance.code:
from sympy import sqrt

def calculate_distance(point1, point2):
    if len(point1) != 2 or len(point2) != 2:
        return "Error: Each point must have exactly 2 coordinates."
    x1, y1 = point1
    x2, y2 = point2
    distance = sqrt((x2 - x1)**2 + (y2 - y1)**2)
    return distance.evalf()

calculate_geometric_progression.code:
def calculate_geometric_progression():
    largest_geo_number = 0

    # Loop through digit a from 9 to 1
    for a in range(9, 0, -1):
        for r in range(1, 10):  # Possible values for the common ratio
            b = a * r
            c = a * r * r
            if b < 10 and c < 10:
                # Check if all digits are distinct
                if len(set([a, b, c])) == 3:
                    number = a * 100 + b * 10 + c
                    if number > largest_geo_number:
                        largest_geo_number = number
    return largest_geo_number

calculate_time_of_travel.code:
def calculate_time_of_travel(distance, speed):
    if speed <= 0:
        return 'Error: Speed must be greater than zero.'
    return distance / speed

evaluate_arithmetic_expression.code:
from sympy import sympify, SympifyError

def evaluate_arithmetic_expression(expression):
    try:
        result = sympify(expression)
        return result.evalf() if result.is_number else str(result)
    except SympifyError:
        return 'Error: Invalid expression.'

