from sympy import symbols, Eq, solve

def solve_linear_equation(corn_weight, beans_weight, total_weight, total_cost):
    x = symbols('x')
    y = total_weight - x
    cost = 0.99 * x + 0.45 * y
    if cost == total_cost:
        return (x, y)
    return None
