from sympy import symbols, solve

def calculate_specific_statistics(corn_weight, beans_weight, total_weight, total_cost):
    x = symbols('x')
    y = total_weight - x
    cost = 0.99 * x + 0.45 * y
    return (cost, solve(cost - total_cost, x))

