from sympy import symbols, Eq, solve

def solve_system_of_equations(equation1, equation2):
    x, y = symbols('x y')
    eq1 = Eq(*map(eval, equation1.split('=')))
    eq2 = Eq(*map(eval, equation2.split('=')))
    return solve((eq1, eq2), (x, y))

