def calculate_final_cost(corn_price, beans_price, pounds_of_corn, pounds_of_beans):
    total_cost = (corn_price * pounds_of_corn) + (beans_price * pounds_of_beans)
    return total_cost

def calculate_average_price(price1, quantity1, price2, quantity2):
    total_cost = (price1 * quantity1 + price2 * quantity2) / (quantity1 + quantity2)
    return total_cost

from sympy import sympify, SympifyError

def calculate_expression_result(expression):
    try:
        result = sympify(expression)
        # Return as float if it is a number, else return the expression as a string
        return float(result) if result.is_number else str(result)
    except (SympifyError, ValueError) as e:
        return str(e)

from sympy import sympify, SympifyError

def evaluate_expression(expression):
    try:
        result = sympify(expression)
        return float(result) if result.is_number else str(result)
    except (SympifyError, ValueError) as e:
        return str(e)
