from sympy import Rational

def calculate_probability_using_counts(total_count, favorable_count):
    '''Calculate the probability from total and favorable counts.'''  
    if total_count <= 0:
        raise ValueError('Total count must be greater than zero.')
    return Rational(favorable_count, total_count).limit_denominator()

def calculate_probability(total_outcomes, favorable_outcomes):
    from sympy import Rational
    if total_outcomes <= 0:
        raise ValueError('Total outcomes must be a positive integer')
    return Rational(favorable_outcomes, total_outcomes).limit_denominator()
