def calculate_expected_rolls_in_year(daily_expected_rolls):
    total_days = 365
    if not isinstance(daily_expected_rolls, int) or daily_expected_rolls < 0:
        raise ValueError('daily_expected_rolls must be a non-negative integer')
    total_expected_rolls = daily_expected_rolls * total_days
    return total_expected_rolls

