def calculate_expected_rolls_in_year(daily_expected_rolls, year_length):
    if not isinstance(daily_expected_rolls, int) or daily_expected_rolls < 0:
        raise ValueError('daily_expected_rolls must be a non-negative integer')
    if not isinstance(year_length, int) or year_length <= 0:
        raise ValueError('year_length must be a positive integer')
    return daily_expected_rolls * year_length

calculate_probability.code:
def calculate_probability(total_outcomes, favorable_outcomes):
    from sympy import Rational
    if total_outcomes <= 0:
        raise ValueError('Total outcomes must be a positive integer')
    return Rational(favorable_outcomes, total_outcomes).limit_denominator()

calculate_probability_of_at_least_two_id.code:
def calculate_probability_of_at_least_two_identical_rolls(number_of_dice, sides_per_die):
    from sympy import Rational, factorial
    if number_of_dice <= 0:
        raise ValueError('Number of dice must be positive.')
    if sides_per_die <= 1:
        raise ValueError('Each die must have at least 2 sides.')

    total_outcomes = sides_per_die ** number_of_dice
    favorable_outcomes = factorial(sides_per_die) / factorial(sides_per_die - number_of_dice) if number_of_dice <= sides_per_die else 0

    return 1 - Rational(favorable_outcomes, total_outcomes)

