from sympy import Rational

def calculate_probability_of_non_six_dice(total_rolls, six_sided):
    if six_sided:
        return Rational(1, 6) * total_rolls
    else:
        # Assuming two-color scenario, update accordingly
        return Rational(3, 12) * total_rolls

