from math import factorial

def calculate_arrangements(total_items, duplicates):
    denominator = 1
    for count in duplicates.values():
        denominator *= factorial(count)
    return factorial(total_items) // denominator

from sympy import Rational

def calculate_expected_rolls_per_year(days, rolls_per_day):
    return days * Rational(rolls_per_day)
