from sympy import Rational

def calculate_probability_of_dice_rolls(num_dice, success_condition):
    total_outcomes = 6 ** num_dice
    favorable_outcomes = eval(success_condition)  # Assuming success_condition is safe to eval
    return Rational(favorable_outcomes, total_outcomes)

