from math import comb

def calculate_combinations(n, k):
    if k > n:
        return 0
    return comb(n, k)

calculate_factorial.code:
from math import factorial

def calculate_factorial(n):
    if n < 0:
        raise ValueError('Factorial is not defined for negative numbers')
    return factorial(n)

evaluate_complementary_probability.code:
from sympy import Rational

def evaluate_complementary_probability(total_outcomes, favorable_outcomes, trials):
    return 1 - (Rational(favorable_outcomes, total_outcomes) ** trials)

