from sympy import Rational

def calculate_probability_of_dice_rolls(num_dice, success_condition, faces=6):
    total_outcomes = faces ** num_dice
    favorable_outcomes = eval(success_condition) if isinstance(success_condition, str) else success_condition
    return Rational(favorable_outcomes, total_outcomes)

