from sympy import factorial

def calculate_permutations_with_repeats(total_items, repeated_items_counts):
    denominator = 1
    for count in repeated_items_counts.values():
        denominator *= factorial(count)
    return factorial(total_items) // denominator

calculate_probability_of_event.code:
from sympy import Rational

def calculate_probability_of_event(favorable_outcomes, total_outcomes):
    if total_outcomes == 0:
        return 0
    return Rational(favorable_outcomes, total_outcomes)

