from sympy import Rational

def calculate_probability(favorable_outcomes, total_outcomes):
    if total_outcomes == 0:
        return 0
    return Rational(favorable_outcomes, total_outcomes).limit_denominator()

from sympy import factorial

def calculate_unique_permutations(total_items, item_counts):
    denominator = 1
    for count in item_counts.values():
        denominator *= factorial(count)
    return factorial(total_items) // denominator
