from sympy import pi, sqrt

def calculate_area_and_volume_of_shapes(shape_type, dimensions):
    if shape_type == 'circle':
        radius = dimensions['radius']
        area = pi * radius ** 2
        volume = None
        return {'area': area, 'volume': None}
    elif shape_type == 'cone':
        radius = dimensions['radius']
        height = dimensions['height']
        area = pi * radius * sqrt(radius**2 + height**2) + pi * radius**2  # lateral + base
        volume = (1/3) * pi * radius**2 * height
        return {'area': area, 'volume': volume}
    elif shape_type == 'rectangle':
        width = dimensions['width']
        height = dimensions['height']
        area = width * height
        volume = None
        return {'area': area, 'volume': None}
    else:
        return {'area': None, 'volume': None}

from sympy import pi

def calculate_area_of_circle(radius):
    return pi * radius ** 2

from sympy import pi

def calculate_volume_of_cone(radius, height):
    return (1/3) * pi * radius ** 2 * height
