def calculate_volume_of_cone(radius, height):
    from sympy import pi
    return (1/3) * pi * radius ** 2 * height

def calculate_area_of_circle(radius):
    from sympy import pi
    return pi * radius ** 2

def calculate_area_of_frustum(radius1, radius2, height):
    import sympy as sp
    return sp.pi * (radius1 + radius2) * sp.sqrt((radius2-radius1)**2 + height**2) + sp.pi * (radius1**2 + radius2**2)
