from sympy import pi, simplify

def calculate_area_of_circle(radius):
    if radius <= 0:
        raise ValueError("Radius must be a positive number.")
    return simplify(pi * (radius ** 2))

calculate_area_of_shapes.code:
from sympy import pi, sqrt
def calculate_area_of_shapes(shape_type, dimensions):
    if shape_type == 'circle':
        radius = dimensions['radius']
        return pi * radius ** 2
    elif shape_type == 'rectangle':
        length = dimensions['length']
        width = dimensions['width']
        return length * width
    elif shape_type == 'triangle':
        base = dimensions['base']
        height = dimensions['height']
        return (1/2) * base * height
    else:
        return 'Shape not supported.'

calculate_area_of_triangle.code:
from sympy import sqrt

def calculate_area_of_triangle(side_a, side_b, side_c):
    s = (side_a + side_b + side_c) / 2
    return sqrt(s * (s - side_a) * (s - side_b) * (s - side_c))

calculate_surface_area_of_cylinder.code:
from sympy import pi

def calculate_surface_area_of_cylinder(radius, height):
    return 2 * pi * radius * (height + radius)

calculate_surface_area_of_frustum.code:
from sympy import pi, sqrt

def calculate_surface_area_of_frustum(radius_lower, radius_upper, height):
    slant_height = sqrt(height**2 + (radius_upper - radius_lower)**2)
    return pi * (radius_lower + radius_upper) * slant_height + pi * (radius_lower**2 + radius_upper**2)

calculate_visible_part_of_circle.code:
from sympy import pi

def calculate_visible_part_of_circle(diameter):
    radius = diameter / 2
    return (1 / 2) * pi * (radius ** 2)

