def calculate_volume_of_cone(radius, height):
    from sympy import pi
    return (1/3) * pi * (radius ** 2) * height

from sympy import pi

def calculate_area_of_circle(radius):
    return pi * (radius ** 2)

def calculate_surface_area_of_frustum(radius_lower, radius_upper, height):
    from sympy import pi, sqrt
    slant_height = sqrt(height**2 + (radius_upper - radius_lower)**2)
    return pi * (radius_lower + radius_upper) * slant_height + pi * (radius_lower**2 + radius_upper**2)
