calculate_area_of_circle.code:
from sympy import pi, simplify

def calculate_area_of_circle(radius):
    if radius <= 0:
        raise ValueError("Radius must be a positive number.")
    return simplify(pi * (radius ** 2))

calculate_area_of_triangle.code:
from sympy import sqrt

def calculate_area_of_triangle(side_a, side_b, side_c):
    s = (side_a + side_b + side_c) / 2
    return sqrt(s * (s - side_a) * (s - side_b) * (s - side_c))

calculate_surface_area_of_cylinder.code:
from sympy import pi

def calculate_surface_area_of_cylinder(radius, height):
    return 2 * pi * radius * (height + radius)

calculate_volume_of_cone.code:
from sympy import pi, simplify

def calculate_volume_of_cone(radius, height):
    if radius <= 0 or height <= 0:
        raise ValueError("Radius and height must be positive numbers.")
    return simplify((1/3) * pi * (radius ** 2) * height)
