calculate_area_of_shape.code:
import math

def calculate_area_of_shape(shape_type, parameters):
    if shape_type.lower() == 'circle':
        radius = parameters['radius']
        return math.pi * radius ** 2
    elif shape_type.lower() == 'triangle':
        base = parameters['base']
        height = parameters['height']
        return 0.5 * base * height
    elif shape_type.lower() == 'trapezoid':
        base1 = parameters['base1']
        base2 = parameters['base2']
        height = parameters['height']
        return ((base1 + base2) * height) / 2
    else:
        return 'Invalid shape type.'

calculate_area_of_triangle.code:
from sympy import sqrt

def calculate_area_of_triangle(a, b, c):
    if a <= 0 or b <= 0 or c <= 0:
        return 'Invalid triangle sides.'
    s = (a + b + c) / 2
    area = sqrt(s * (s - a) * (s - b) * (s - c))
    return area.evalf()

