from sympy import pi

def calculate_area_of_frustum(radius_top, radius_bottom, height):
    volume = (pi * height / 3) * (radius_top ** 2 + radius_bottom ** 2 + radius_top * radius_bottom)
    surface_area = pi * (radius_top + radius_bottom) * (height + radius_top + radius_bottom)
    return volume, surface_area;

