from sympy import sqrt

def calculate_area_of_triangle(a, b, c):
    if a <= 0 or b <= 0 or c <= 0:
        return 'Invalid triangle sides.'
    s = (a + b + c) / 2
    return sqrt(s * (s - a) * (s - b) * (s - c)).evalf()

