import math

def volume_of_sphere(radius):
    return (4/3) * math.pi * (radius ** 3)

from sympy import sympify, SympifyError

def evaluate_expression(expression):
    try:
        result = sympify(expression)
        if result.is_number:
            result = float(result)
        else:
            result = str(result)
        return result
    except SympifyError as e:
        return str(e)

import math

def calculate_right_triangle_leg(leg, hypotenuse):
    """Calculate the missing leg of a right triangle given one leg and the hypotenuse."""
    remaining_leg = math.sqrt(hypotenuse**2 - leg**2)
    return remaining_leg if remaining_leg.is_integer() else round(remaining_leg, 2)
