from sympy import sympify, SympifyError
from sympy.parsing.sympy_parser import parse_expr, standard_transformations, implicit_multiplication_application

def evaluate_expression(expression):
    # Transformations to enable parsing and handle implicit multiplication
    transformations = (standard_transformations + (implicit_multiplication_application,))
    try:
        parsed_expr = parse_expr(expression, transformations=transformations)
        # Use Evalf to deal with Float outputs for accurate precision
        result = parsed_expr.evalf()
        return result
    except (SympifyError, SyntaxError) as e:
        return str(e)
