import math

def calculate_distance_3d(point1, point2):
    x1, y1, z1 = point1
    x2, y2, z2 = point2
    try:
        distance = math.sqrt((x2 - x1)**2 + (y2 - y1)**2 + (z2 - z1)**2)
    except Exception as e:
        return str(e)
    return distance

