from sympy import sympify, SympifyError
from sympy.parsing.sympy_parser import parse_expr, standard_transformations, implicit_multiplication_application

def evaluate_expression(expression):
    # Ensure whitespace stripping
    expression = expression.strip()
    # Transformations to allow implicit multiplication and standard parsing
    transformations = (standard_transformations + (implicit_multiplication_application,))
    try:
        parsed_expr = parse_expr(expression, transformations=transformations)
        result = parsed_expr.simplify()
        if result.is_number:
            result = float(result)
        else:
            result = str(result)
            if 'I' not in result:  # Check to prevent imaginary numbers in basic algebra calculations
                # Ensure that radicals are represented in simplified fractional form
                result = result.evalf() if parsed_expr.is_number else result
        return result
    except (SympifyError, SyntaxError, ValueError) as e:
        return str(e)
