from sympy import symbols, solve, SympifyError

def evaluate_math_problem(problem_statement):
    try:
        x, y, z = symbols('x y z')
        # Using a safer way to evaluate a math expression
        locals_dict = {'x': x, 'y': y, 'z': z, 'solve': solve}
        expr = eval(problem_statement, {"__builtins__": None}, locals_dict)  
        return expr
    except SympifyError as e:
        return str(e)
    except Exception as e:
        return str(e)
