from sympy import symbols, Eq, solve, sqrt, pi, Rational, simplify, evalf, sympify

def evaluate_math_problem(problem_statement):
    try:
        # Supported variables (expand as needed for the problems solved)
        x, y, z = symbols('x y z')
        expr = sympify(problem_statement, evaluate=False)  # Safely parse the problem statement
        return expr
    except Exception as e:
        return str(e)

from sympy import symbols, Eq, solve

def solve_math_problem(equations, variables):
    try:
        # Declaring variables
        var_symbols = symbols(variables)
        # Creating sympy equations
        eq_objects = [Eq(*map(sympify, eq.split('='))) for eq in equations]
        # Solving equations
        solutions = solve(eq_objects, var_symbols)
        return solutions
    except Exception as e:
        return str(e)
