import math

def solve_trigonometric_triangle(angle_type, known_side, known_value, angle_degree):
    # Convert the angle from degrees to radians
    angle_radian = math.radians(angle_degree)

    # Calculate the missing side using the given trigonometric function
    if angle_type == 'sine':
        if known_side == 'opposite':
            # Solve for hypotenuse
            return known_value / math.sin(angle_radian)
        elif known_side == 'hypotenuse':
            # Solve for opposite
            return known_value * math.sin(angle_radian)
    elif angle_type == 'cosine':
        if known_side == 'adjacent':
            # Solve for hypotenuse
            return known_value / math.cos(angle_radian)
        elif known_side == 'hypotenuse':
            # Solve for adjacent
            return known_value * math.cos(angle_radian)
    elif angle_type == 'tangent':
        if known_side == 'opposite':
            # Solve for adjacent
            return known_value / math.tan(angle_radian)
        elif known_side == 'adjacent':
            # Solve for opposite
            return known_value * math.tan(angle_radian)
    else:
        raise ValueError("Invalid angle type provided.")
