from sympy import symbols, Eq, solve, sympify

def solve_math_problem_fixed(equations, variables):
    try:
        # Declaring variables
        var_symbols = symbols(variables)
        # Creating sympy equations
        eq_objects = [Eq(*map(sympify, eq.split('='))) for eq in equations]
        # Solving equations
        solutions = solve(eq_objects, var_symbols)
        return solutions
    except Exception as e:
        return str(e)
