from sympy import sympify, SympifyError

def evaluate_expression(expression):
    try:
        result = sympify(expression)
        if result.is_number:
            result = float(result)
        else:
            result = str(result)
        return result
    except SympifyError as e:
        return str(e)

from sympy import symbols, Eq, solve, sympify, SympifyError, sin, cos, tan, simplify, cancel

def evaluate_trigonometric(expression, variable_values={}):
    try:
        # Split expressions if they are separated by commas or semicolons
        exprs = [sympify(e.strip(), evaluate=False) for e in expression.split(',')]
        exprs = sum([e.split(';') if isinstance(e, str) else [e] for e in exprs], [])
        solutions = []
        for expr in exprs:
            # equations might already be sympified in some cases
            if isinstance(expr, str):
                expr = sympify(expr)
            expr = simplify(cancel(expr))
            if isinstance(expr, Eq):
                solutions.append(solve(expr, dict=True))
            elif variable_values:
                expr = expr.subs(variable_values)
                solutions.append(expr)
            else:
                solutions.append(expr)
        return solutions
    except SympifyError as e:
        return {'error': str(e)}
    except Exception as error:
        return {'error': str(error)}

from sympy import symbols, Eq, solve, sympify, SympifyError, simplify, cancel

def evaluate_algebra(expression, variable_values={}):
    try:
        # Normalize and parse the expression into individual parts safely
        # This should include converting single symbols into expressions
        # with appropriate substitutions from `variable_values`
        expressions = expression.replace("=", "==").replace(";", ",").split(',')

        exprs = []
        for expr in expressions:
            expr = expr.strip()
            if expr:  # Avoid empty entries
                try:
                    exprs.append(sympify(expr, locals=variable_values, evaluate=False))
                except SympifyError:
                    return {'error': f'Failed to sympify expression: {expr}'}

        solutions = []
        for expr in exprs:
            expr = simplify(cancel(expr))  # Simplify the expression
            if isinstance(expr, Eq):
                solutions.append(solve(expr, dict=True))
            elif variable_values:  # We have some variables to substitute
                expr = expr.subs(variable_values)
                solutions.append(expr)
            else:
                solutions.append(expr)

        return solutions
    except Exception as error:
        return {'error': str(error)}
