from sympy import sympify, SympifyError

def evaluate_expression(expression):
    try:
        result = sympify(expression)
        if result.is_number:
            result = float(result)
        else:
            result = str(result)
        return result
    except SympifyError as e:
        return str(e)

from sympy import symbols, Eq, solve, sympify, sin, cos, tan, simplify, cancel, SympifyError, sqrt, pi

def evaluate_trigonometric(expression, variable_values={}):
    try:
        # Split expressions if they are separated by commas or semicolons
        expr_list = [e.strip() for part in expression.split(',') for e in part.split(';')]
        solutions = []
        for expr in expr_list:
            try:
                parsed_expr = sympify(expr, evaluate=False)
                parsed_expr = parsed_expr.rewrite(sin).rewrite(cos).rewrite(tan)
                parsed_expr = simplify(cancel(parsed_expr))
                if isinstance(parsed_expr, Eq):
                    solutions.append(solve(parsed_expr, dict=True))
                elif variable_values:
                    parsed_expr = parsed_expr.subs(variable_values)
                    solutions.append(parsed_expr)
                else:
                    solutions.append(parsed_expr)
            except SympifyError as se:
                solutions.append({'error': f'Sympify error: {str(se)}'})
            except Exception as e:
                solutions.append({'error': str(e)})
        return solutions
    except Exception as outer_error:
        return {'error': str(outer_error)}

from sympy import sympify, SympifyError, symbols, Eq, solve, simplify, cancel, Rational

def evaluate_algebra(expression, variable_values={}):
    try:
        expressions = expression.replace('\n', '').replace('==', '=').replace('=', '==').replace(';', ',').split(',')
        expressions = [e.strip() for e in expressions if e.strip()]
        exprs = []

        for expr in expressions:
            expr = expr.strip()
            try:
                sym_expr = sympify(expr, locals=variable_values, evaluate=False)
                exprs.append(sym_expr)
            except SympifyError as e:
                return {'error': f'Failed to sympify expression: {expr}. Error: {str(e)}'}

        solutions = []
        for expr in exprs:
            expr = simplify(cancel(expr))
            if isinstance(expr, Eq):
                solutions.append(solve(expr, dict=True))
            elif variable_values:
                expr = expr.subs(variable_values)
                solutions.append(expr)
            else:
                solutions.append(expr)

        return solutions
    except Exception as error:
        return {'error': 'Overall expression error: ' + str(error)}
