import math

def gcd(a, b):
    return math.gcd(a, b)

def calculate_remainder(number, divisor):
    if divisor == 0:
        return 'Division by zero is undefined'
    return number % divisor

def convert_to_base(number, base):
    if base <= 1:
        return 'Base must be greater than 1'
    if number == 0:
        return str(0)
    digits = []
    while number:
        digits.append(int(number % base))
        number //= base
    return ''.join(str(x) for x in digits[::-1])

def sum_of_digits_in_base(number, base):
    if base <= 1:
        return 'Base must be greater than 1'
    total = 0
    digits = convert_to_base(number, base)
    for digit in digits:
        total += int(digit)
    return total
