def convert_decimal_to_base(number, base):
    if base <= 1:
        return 'Base must be greater than 1'
    if number == 0:
        return str(0)
    digits = []
    while number:
        digits.append(int(number % base))
        number //= base
    return ''.join(str(x) for x in digits[::-1])

def calculate_distinct_remainders_sum(start, end, modulus):
    if end < start:
        return 0
    distinct_remainders = set()
    for n in range(start, end + 1):
        square = n ** 2
        remainder = square % modulus
        distinct_remainders.add(remainder)
    return sum(distinct_remainders)

def calculate_remainder(number, modulus):
    if modulus == 0:
        return 'Division by zero is not allowed.'
    return number % modulus

def remainder_and_quotient(number, modulus):
    if modulus == 0:
        return 'Division by zero is not allowed.', None
    remainder = number % modulus
    quotient = number // modulus
    return remainder, quotient
