def convert_decimal_to_base(number, base):
    if base <= 1:
        return 'Base must be greater than 1'
    if number == 0:
        return str(0)
    digits = []
    while number:
        digits.append(int(number % base))
        number //= base
    return ''.join(str(x) for x in digits[::-1])

def calculate_distinct_remainders_sum(start, end, modulus):
    if end < start:
        return 0
    distinct_remainders = set()
    for n in range(start, end + 1):
        square = n**2
        remainder = square % modulus
        distinct_remainders.add(remainder)
    return sum(distinct_remainders), distinct_remainders

def modular_inverse(a, m):
    if a < 1 or m <= 1:
        return None  # Inverse doesn't exist if a is less than 1 or modulus is less than or equal to 1
    gcd, x, _ = sp.gcdex(a, m)
    return x % m if gcd == 1 else None

def convert_from_decimal(number, base):
    if number < 0:
        return '-' + convert_from_decimal(-number, base)
    if base <= 1:
        return 'Base must be greater than 1'
    if number == 0:
        return '0'
    digits = []
    while number:
        digits.append(int(number % base))
        number //= base
    return ''.join(str(x) for x in digits[::-1])
