def calculate_remainders(start, end, modulus):
    remainders = set()
    for n in range(start, end + 1):
        square = n**2
        remainder = square % modulus
        remainders.add(remainder)
    return sum(remainders), len(remainders)

convert_decimal_to_base.code:
def convert_decimal_to_base(number, base):
    if base <= 1:
        return 'Base must be greater than 1'
    if number == 0:
        return str(0)
    digits = []
    while number:
        digits.append(int(number % base))
        number //= base
    return ''.join(str(x) for x in digits[::-1])

