def decimal_to_base(number, base):
    if number == 0:
        return '0'
    if base < 2:
        raise ValueError('Base must be >= 2')
    digits = []
    while number:
        digits.append(int(number % base))
        number //= base
    return ''.join(str(x) for x in digits[::-1])

def convert_decimal_to_base_general(number, base):
    if number < 0:
        raise ValueError("Number must be non-negative.")
    if base < 2:
        raise ValueError("Base must be >= 2.")
    if number == 0:
        return '0'
    digits = []
    while number:
        digits.append(int(number % base))
        number //= base
    return ''.join(str(x) for x in digits[::-1])
