from sympy import sympify, Rational

def calculate_decimal_value(symbolic_value):
    return sympify(symbolic_value).evalf() if sympify(symbolic_value).is_real else str(symbolic_value)

from sympy import sympify, SympifyError

def evaluate_expression(expression):
    try:
        result = sympify(expression)
        if result.is_number:
            result = float(result)
        else:
            result = str(result)
        return result
    except SympifyError as e:
        return str(e)

from sympy import sqrt

def calculate_pythagorean_distance(AB, BC):
    if AB < 0 or BC < 0:
        return 'Length values must be non-negative.'
    return float(sqrt(AB**2 + BC**2))

def calculate_area_of_triangle(base, height):
    if base < 0 or height < 0:
        return 'Base and height must be non-negative values.'
    return (base * height) / 2
