from sympy import N

def evaluate_and_round_expression(expression, decimal_places):
    result = N(expression)
    if decimal_places is not None:
        result = round(result, decimal_places)
    return result

def calculate_max_angle_pentagon(angle_A, angle_B, angle_C, angle_D, angle_E):
    angles = [angle_A, angle_B, angle_C, angle_D, angle_E]
    return max(angles)

def sum_of_angles_pentagon(angle_A, angle_B, x):
    angle_C = x
    angle_D = x
    angle_E = 2 * x + 15
    angles = [angle_A, angle_B, angle_C, angle_D, angle_E]
    return max(angles)

from sympy import sqrt

def calculate_length(point_A, point_B):
    return sqrt((point_B['x'] - point_A['x'])**2 + (point_B['y'] - point_A['y'])**2)
