def sum_of_angles_pentagon(angle_A, angle_B, x):
    angle_C = x
    angle_D = x
    angle_E = 2 * x + 15
    angles = [angle_A, angle_B, angle_C, angle_D, angle_E]
    return max(angles)

def calculate_honda_sales(total_cars, percentage_audi, percentage_toyota, percentage_acura):
    total_percentage = percentage_audi + percentage_toyota + percentage_acura
    if total_percentage > 100:
        raise ValueError('Percentages cannot exceed 100%')
    percentage_honda = 100 - total_percentage
    number_of_hondas = (percentage_honda / 100) * total_cars
    return int(round(number_of_hondas))
