from sympy import Rational

def calculate_fraction_of_total(total_amount, percentage):
    return Rational(percentage, 100) * total_amount

def calculate_honda_sales(total_cars, percentage_audi, percentage_toyota, percentage_acura, percentage_honda=0):
    total_percentage = percentage_audi + percentage_toyota + percentage_acura + percentage_honda
    if total_percentage > 100:
        raise ValueError('Percentages cannot exceed 100%')
    number_of_hondas = (100 - total_percentage) / 100 * total_cars
    return int(round(number_of_hondas))

