from sympy import Rational

def calculate_fraction_of_total(total_amount, percentage):
    return Rational(percentage, 100) * total_amount

def calculate_honda_sales(total_cars, percentage_audi, percentage_toyota, percentage_acura, percentage_honda=0):
    total_percentage = percentage_audi + percentage_toyota + percentage_acura + percentage_honda
    if total_percentage > 100:
        raise ValueError('Percentages cannot exceed 100%')
    number_of_hondas = (100 - total_percentage) / 100 * total_cars
    return int(round(number_of_hondas))

from sympy import primerange

def calculate_remainder(n, m):
    if n < 1 or m < 1:
        raise ValueError('n and m must be positive integers')
    primes = list(primerange(1, 100))  # get prime numbers below 100
    if n > len(primes):
        raise ValueError('Not enough prime numbers available')
    if m > len(primes):
        raise ValueError('m exceeds the number of available primes')
    sum_of_primes = sum(primes[:n])  # sum of the first n primes
    return sum_of_primes % primes[m-1]  # remainder when divided by the mth prime

from sympy import Rational

def calculate_percentage(count, total):
    if total <= 0:
        raise ValueError('Total must be a positive integer')
    return Rational(count, total) * 100

