from sympy import primerange

def calculate_remainder(n, m):
    if n < 1 or m < 1:
        raise ValueError('n and m must be positive integers')
    primes = list(primerange(1, 100))  # get prime numbers below 100
    if n > len(primes):
        raise ValueError('Not enough prime numbers available')
    sum_of_primes = sum(primes[:n])  # sum of the first n primes
    if m > len(primes):
        raise ValueError('m exceeds the number of available primes')
    return sum_of_primes % primes[m-1]  # remainder when divided by the mth prime

