def calculate_probability(favorable_outcomes, total_outcomes, output_fraction=False):
    from sympy import Rational
    if total_outcomes <= 0:
        return 'Total outcomes must be greater than zero.'  # Error handling
    if favorable_outcomes < 0:
        return 'Favorable outcomes cannot be negative.'  # Error handling
    result = Rational(favorable_outcomes, total_outcomes)
    if output_fraction:
        return result  # Return as a fraction
    else:
        return float(result)  # Return as a decimal
